Create Proc ModifyNote
	@Name		NVARCHAR(25),
	@Email		NVARCHAR(100),
	@Title		NVARCHAR(150),
	@ModifyIP	NVARCHAR(15),
	@Content	NText,
	@RegiNum	NVARCHAR(30),
	@Encoding	NVARCHAR(10),
	@Homepage	NVARCHAR(100),
	@FileName	NVARCHAR(255),
	@FileSize	INT,

	@Id INT
AS

